import React, { useState, useRef } from "react";
import { useDispatch, useSelector } from "react-redux";
import Message from "../Component/Message";
import { Row, Col, Container, ListGroup } from "react-bootstrap";
import {
  CRow,
  CCol,
  CForm,
  CFormInput,
  CInputGroup,
  CInputGroupText,
  CCardBody,
  CFormSelect,
  CCard,
  CAlert,
} from "@coreui/react";
import { uploadProject } from "../Action/projectActions";
const Dashboard = () => {
  const [imageUrl, setImageUrl] = useState("");
  const [name, setName] = useState("");
  const [uploadedFileName, setUploadedFileName] = useState(null);
  const [productImage, setProductImage] = useState("");
  const inputRef = useRef(null);
  const dispatch = useDispatch();
  const addProject = useSelector((state) => state.addProject);
  const { load, project, err } = addProject;
  const userLogin = useSelector((state) => state.userLogin);
  const { loading, userInfo, error } = userLogin;
  return (
    <>
      <Container>
        <Row>
          <Col md={8}>
            <CRow>
              <CCol md={12}>
                {err && (
                  <CAlert color='danger' style={{ textAlign: "center" }}>
                    {err}
                  </CAlert>
                )}
              </CCol>
            </CRow>
            <CRow>
              <CCol md={12}>
                {project && (
                  <CAlert color='success' style={{ textAlign: "center" }}>
                    {project.message}
                  </CAlert>
                )}
              </CCol>
            </CRow>
            <CRow className='justify-content-center'>
              <CCol md={12}>
                <p className='text-gray-800 dark:text-gray-200 text-xl font-bold'>
                  Add New Product
                </p>
              </CCol>
              <CCol md={12} className='bg-white rounded-lg'>
                <CForm className='row mx-4 g-3'>
                  <CCol md={6}>
                    <CInputGroup className='mb-3'>
                      <CInputGroupText>
                        <i class='fas fa-signature'></i>
                      </CInputGroupText>
                      <CFormInput
                        placeholder='Enter Name'
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                      />
                    </CInputGroup>
                  </CCol>

                  <CRow className='my-4'>
                    <CCol md={6}>
                      <input
                        ref={inputRef}
                        onChange={handleDisplayFileDetails}
                        type='file'
                      />
                    </CCol>
                  </CRow>
                  <CRow className='flex items-center justify-end my-4'>
                    <CCol md={3} xs={8}>
                      <button
                        onClick={submitHandler}
                        className='py-2 px-4 mt-2 bg-indigo-600 hover:bg-indigo-700 focus:ring-indigo-500 focus:ring-offset-indigo-200 text-white w-full transition ease-in duration-200 text-center text-base font-semibold shadow-md focus:outline-none focus:ring-2 focus:ring-offset-2  rounded-lg '
                      >
                        {loading ? "Loading" : "Add Product"}
                      </button>
                    </CCol>
                  </CRow>
                </CForm>
              </CCol>
            </CRow>
          </Col>
          <Col md={4}>
            <ListGroup variant='flush'>
              <ListGroup.Item className='text-center'>
                <h2>User Profile</h2>
              </ListGroup.Item>
              <ListGroup.Item>
                <Row>
                  <Col>Name:</Col>
                  <Col>{userInfo.name}</Col>
                </Row>
              </ListGroup.Item>
              <ListGroup.Item>
                <Row>
                  <Col>Email :</Col>
                  <Col>{userInfo.email}</Col>
                </Row>
              </ListGroup.Item>

              <ListGroup.Item>
                {error && <Message variant='danger'>{error}</Message>}
              </ListGroup.Item>
            </ListGroup>
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default Dashboard;
