import React, { useState, useRef } from "react";
import { useDispatch, useSelector } from "react-redux";
import Message from "../Component/Message";
import { Row, Col, Container, ListGroup } from "react-bootstrap";
import {
  CRow,
  CCol,
  CForm,
  CFormInput,
  CInputGroup,
  CInputGroupText,
  CCardBody,
  CFormSelect,
  CCard,
  CAlert,
} from "@coreui/react";
import { uploadProject } from "../Action/projectActions";
const Dashboard = () => {
  const [imageUrl, setImageUrl] = useState("");
  const [name, setName] = useState("");
  const [detail, setDetail] = useState("");
  const [uploadedFileName, setUploadedFileName] = useState(null);
  const [projectImage, setProjectImage] = useState("");
  const inputRef = useRef(null);
  const dispatch = useDispatch();
  const addProject = useSelector((state) => state.addProject);
  const { load, project, err } = addProject;
  const userLogin = useSelector((state) => state.userLogin);
  const { loading, userInfo, error } = userLogin;

  const handleDisplayFileDetails = () => {
    inputRef.current?.files &&
      setUploadedFileName(inputRef.current.files[0].name);
    setProjectImage(inputRef.current.files[0]);
  };

  const submitHandler = async (e) => {
    e.preventDefault();

    if (projectImage && name && detail) {
      console.log("click");
      const formData = new FormData();
      formData.append("productImage", projectImage, projectImage.name);
      formData.append("name", name);
      formData.append("detail", detail);
      formData.append("imageUrl", imageUrl);
      dispatch(uploadProject(formData));
      console.log(formData);
    }
  };
  return (
    <>
      <Container>
        <Row>
          <Col md={8}>
            <CRow>
              <CCol md={12}>
                {err && (
                  <CAlert color='danger' style={{ textAlign: "center" }}>
                    {err}
                  </CAlert>
                )}
              </CCol>
            </CRow>
            <CRow>
              <CCol md={12}>
                {project && (
                  <CAlert color='success' style={{ textAlign: "center" }}>
                    {project.message}
                  </CAlert>
                )}
              </CCol>
            </CRow>
            <CRow className='justify-content-center'>
              <CCol md={12} className='mt-4'>
                <p className='text-gray-800 dark:text-gray-200 text-xl font-bold'>
                  Add New Product
                </p>
              </CCol>
              <CCol md={12} className='bg-white rounded-lg'>
                <CForm className='row  '>
                  <CCol md={6}>
                    <CInputGroup className='mb-3'>
                      <CFormInput
                        placeholder='Enter Name'
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                      />
                    </CInputGroup>
                  </CCol>
                  <CCol md={6}>
                    <CInputGroup className='mb-3'>
                      <CFormInput
                        placeholder='Enter details'
                        value={detail}
                        onChange={(e) => setDetail(e.target.value)}
                      />
                    </CInputGroup>
                  </CCol>

                  <CRow className='my-4'>
                    <CCol md={6}>
                      <input
                        ref={inputRef}
                        onChange={handleDisplayFileDetails}
                        type='file'
                      />
                    </CCol>
                  </CRow>
                  <CRow className='d-flex items-center justify-end my-4'>
                    <CCol md={3} xs={8}>
                      <button
                        onClick={submitHandler}
                        className='py-2 px-4 mt-2 bg-indigo-600 hover:bg-indigo-700 focus:ring-indigo-500 focus:ring-offset-indigo-200  w-full transition ease-in duration-200 text-center text-base font-semibold shadow-md focus:outline-none focus:ring-2 focus:ring-offset-2  rounded '
                      >
                        {load ? "Loading" : "Add Project"}
                      </button>
                    </CCol>
                  </CRow>
                </CForm>
              </CCol>
            </CRow>
          </Col>
          <Col md={4}>
            <ListGroup variant='flush'>
              <ListGroup.Item className='text-center'>
                <h2>User Profile</h2>
              </ListGroup.Item>
              <ListGroup.Item>
                <Row>
                  <Col>Name:</Col>
                  <Col>{userInfo.name}</Col>
                </Row>
              </ListGroup.Item>
              <ListGroup.Item>
                <Row>
                  <Col>Email :</Col>
                  <Col>{userInfo.email}</Col>
                </Row>
              </ListGroup.Item>

              <ListGroup.Item>
                {error && <Message variant='danger'>{error}</Message>}
              </ListGroup.Item>
            </ListGroup>
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default Dashboard;
