import axios from "axios";
import {
  PROJECT_ADD_REQUEST,
  PROJECT_ADD_SUCCESS,
  PROJECT_ADD_FAIL,
} from "../Constant/projectConstant";
export const uploadProduct = (formData) => async (dispatch, getState) => {
  try {
    dispatch({
      type: PROJECT_ADD_REQUEST,
    });

    const { data } = await axios.post(
      "/project/dashboard/addProject",
      formData
    );

    dispatch({
      type: PROJECT_ADD_SUCCESS,
      payload: data,
    });
  } catch (error) {
    dispatch({
      type: PROJECT_ADD_FAIL,
      payload:
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    });
  }
};
