import React, { useState, useRef, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import "@coreui/coreui/dist/css/coreui.min.css";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/css/bootstrap.min.css";
import Message from "../Component/Message";
import { Row, Col, Container, ListGroup, Button } from "react-bootstrap";
import {
  CRow,
  CCol,
  CForm,
  CFormInput,
  CInputGroup,
  CInputGroupText,
  CCardBody,
  CFormSelect,
  CCard,
  CAlert,
} from "@coreui/react";
import { uploadProject, displayAllProject } from "../Action/projectActions";
const Dashboard = () => {
  const [name, setName] = useState("");
  const [detail, setDetail] = useState("");
  const [uploadedFileName, setUploadedFileName] = useState(null);
  const [projectImage, setProjectImage] = useState("");
  const inputRef = useRef(null);
  const dispatch = useDispatch();
  const allProject = useSelector((state) => state.allProject);
  const { projects, errors } = allProject;
  const addProject = useSelector((state) => state.addProject);
  const { load, project, err } = addProject;
  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo, error } = userLogin;

  useEffect(() => {
    dispatch(displayAllProject());
  }, [dispatch, load]);
  const handleDisplayFileDetails = () => {
    inputRef.current?.files &&
      setUploadedFileName(inputRef.current.files[0].name);
    setProjectImage(inputRef.current.files[0]);
  };

  const submit = async (e) => {
    e.preventDefault();

    if (projectImage && name && detail) {
      const formData = new FormData();
      formData.append("projectImage", projectImage, projectImage.name);
      formData.append("name", name);
      formData.append("detail", detail);

      dispatch(uploadProject(formData));
      console.log(formData);
    }
  };
  return (
    <>
      <Container>
        <Row>
          <Col md={8}>
            <CRow>
              <CCol md={12} className='mt-2'>
                {err && (
                  <CAlert color='danger' style={{ textAlign: "center" }}>
                    {err}
                  </CAlert>
                )}
              </CCol>
            </CRow>
            <CRow>
              <CCol md={12} className='mt-2'>
                {errors && (
                  <CAlert color='danger' style={{ textAlign: "center" }}>
                    {errors}
                  </CAlert>
                )}
              </CCol>
            </CRow>
            <CRow>
              <CCol md={12}>
                {project && (
                  <CAlert color='success' style={{ textAlign: "center" }}>
                    {project.message}
                  </CAlert>
                )}
              </CCol>
            </CRow>
            <CRow className='justify-content-center'>
              <CCol md={12} className='mt-4'>
                <p className='text-gray-800 dark:text-gray-200 text-xl font-bold'>
                  Add New Product
                </p>
              </CCol>
              <CCol md={12} className='bg-white rounded-lg'>
                <CForm className='row  '>
                  <CCol md={6}>
                    <CInputGroup className='mb-3'>
                      <CInputGroupText>
                        <i class='fa fa-signature'></i>
                      </CInputGroupText>
                      <CFormInput
                        placeholder='Enter Name'
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                      />
                    </CInputGroup>
                  </CCol>
                  <CCol md={6}>
                    <CInputGroup className='mb-3'>
                      <CInputGroupText>
                        <i class='fa fa-signature'></i>
                      </CInputGroupText>
                      <CFormInput
                        placeholder='Enter details'
                        value={detail}
                        onChange={(e) => setDetail(e.target.value)}
                      />
                    </CInputGroup>
                  </CCol>

                  <CRow className='my-4'>
                    <CCol md={6}>
                      <input
                        ref={inputRef}
                        onChange={handleDisplayFileDetails}
                        type='file'
                      />
                    </CCol>
                  </CRow>
                  <CRow>
                    <CCol md={3} xs={8}>
                      <Button
                        onClick={submit}
                        className='py-2 px-4 mt-2 bg-indigo-600 hover:bg-indigo-700 focus:ring-indigo-500 focus:ring-offset-indigo-200 text-white w-full transition ease-in duration-200 text-center text-base font-semibold shadow-md focus:outline-none focus:ring-2 focus:ring-offset-2  rounded-lg'
                      >
                        {load ? "loading" : "add Project"}
                      </Button>
                    </CCol>
                  </CRow>
                </CForm>
              </CCol>
            </CRow>
          </Col>
          <Col md={4}>
            <ListGroup variant='flush'>
              <ListGroup.Item className='text-center'>
                <h2>User Profile</h2>
              </ListGroup.Item>
              <ListGroup.Item>
                <Row>
                  <Col>Name:</Col>
                  <Col>{userInfo.name}</Col>
                </Row>
              </ListGroup.Item>
              <ListGroup.Item>
                <Row>
                  <Col>Email :</Col>
                  <Col>{userInfo.email}</Col>
                </Row>
              </ListGroup.Item>

              <ListGroup.Item>
                {error && <Message variant='danger'>{error}</Message>}
              </ListGroup.Item>
            </ListGroup>
          </Col>
        </Row>
        <Row>
          <div>
            <h4 className='font-semibold'>All Project</h4>
          </div>
          <div className='flex flex-col'>
            <div className=' py-2 my-2 overflow-x-auto sm:-mx-6 lg:-mx-8'>
              <div className='py-2 align-middle inline-block min-w-full sm:px-6 lg:px-8'>
                <div className='shadow overflow-hidden border-b border-gray-200 sm:rounded-lg'>
                  <table className='min-w-full divide-y divide-gray-200'>
                    <thead className='bg-gray-50'>
                      <tr>
                        <th
                          scope='col'
                          className='px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider'
                        >
                          Name
                        </th>
                        <th
                          scope='col'
                          className='px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider'
                        >
                          Image
                        </th>

                        <th
                          scope='col'
                          className='px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider'
                        >
                          Details
                        </th>
                      </tr>
                    </thead>
                    <tbody className='bg-white divide-y divide-gray-200'>
                      {projects &&
                        projects.map((product) => (
                          <tr key={product._id}>
                            <td className='px-6 py-4 whitespace-nowrap'>
                              <div className='flex items-center'>
                                <div className=''>
                                  <div className='text-sm font-medium text-gray-900'>
                                    {product.name}
                                  </div>
                                </div>
                              </div>
                            </td>
                            <td className='px-2 py-2 whitespace-nowrap'>
                              {/* <div className='flex items-center'>
                                <div className='ml-4'>
                                  <img
                                    style={{
                                      width: "7rem",
                                      height: "7rem",
                                      objectFit: "cover",
                                    }}
                                    src={`${imageUrl}${product.productImage}`}
                                    alt={product.name}
                                  ></img>
                                </div>
                              </div> */}
                            </td>
                            <td className='px-6 py-4 whitespace-nowrap'>
                              <div className='flex items-center'>
                                <div className='ml-4'>
                                  <div className='text-sm font-medium text-gray-900'>
                                    {product.detail}
                                  </div>
                                </div>
                              </div>
                            </td>
                          </tr>
                        ))}
                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          </div>
        </Row>
      </Container>
    </>
  );
};

export default Dashboard;
